function startAccordion(){const c=document.querySelectorAll(".accordion");c&&c.forEach(c=>{c.addEventListener("click",e=>{if(e.target.closest(".accordion__header")){const o=c.querySelectorAll(".accordion__item"),t=e.target.closest(".accordion__header").querySelector(".accordion__icon").classList.contains("active");if(o.forEach(c=>{c.querySelector(".accordion__icon").classList.remove("active"),c.querySelector(".accordion__title").classList.remove("active"),c.querySelector(".accordion__content").style.height="0px"}),!t){const c=e.target.closest(".accordion__item");c.querySelector(".accordion__icon").classList.add("active"),c.querySelector(".accordion__title").classList.add("active"),c.querySelector(".accordion__content").style.height=c.querySelector(".accordion__content").scrollHeight+"px",console.dir(c.querySelector(".accordion__content"))}}})})}export default startAccordion;