function startBurger(){const e=document.querySelector(".header__burger");if(e){const t=document.querySelector(".header__list");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active"),document.body.classList.toggle("lock")}),document.body.addEventListener("click",c=>{c.target.closest(".header")||(t.classList.remove("active"),e.classList.remove("active"),document.body.classList.remove("lock"))})}}export default startBurger;