function startModal(){const t=document.querySelector(".modal");if(t){const a=t.dataset.modalTarget,e=document.querySelector(`[data-modal-path=${a}]`);t.addEventListener("click",a=>{(a.target.closest(".modal__close")||a.target.classList.contains("modal"))&&t.classList.remove("active")}),e.addEventListener("click",()=>{t.classList.add("active")})}}export default startModal;